window.addEventListener("load",function(){let e=document.querySelector(".filters__button");e.addEventListener("click",function(){this.querySelector(".filters__icon").classList.toggle("filters__icon--active");let e=this.nextElementSibling;e.style.maxHeight?e.style.maxHeight=null:e.style.maxHeight=e.scrollHeight+"px"});let t=document.querySelectorAll(".filters__option");t.forEach(l=>{l.addEventListener("click",function(){let e=l.closest(".filters__holder");e.style.maxHeight=null;let t=l.closest(".filters__select");t.querySelector(".filters__icon").classList.toggle("filters__icon--active"),t.querySelector(".filters__text").textContent=l.innerHTML,t.querySelector(".filters__button").value=l.innerHTML})}),document.querySelector(".social__btn").addEventListener("click",function(){document.querySelector(".social__list").classList.toggle("social__list--active")})});